INSERT INTO `school` VALUES ('562', '上海中医药大学', '专业', '上海中医药大学重点专业共有4个专业，其中分为一级学科1个专业和二级学科3个专业，以及国家重点学科是中药学，二级国家重点学科是中医骨伤科学，中医内科学，中医外科学。针灸推拿学，中药学，中医学是比较好的专业。');
