INSERT INTO `问答预存` VALUES ('东华大学', '学校性质', '0', '东华大学（Donghua University），简称“东华”，地处中国上海，是教育部直属、国家“211工程”重点建设的全国重点大学，国家“双一流”世界一流学科建设高校；入选“111计划”、“2011计划”、“卓越工程师教育培养计划”、“海外高层次人才引进计划”、“国家大学生创新性实验计划”、“中非高校20+20合作计划”、“国家级大学生创新创业训练计划”、“国家建设高水平大学公派研究生项目”，是高水平行业特色大学优质资源共享联盟成员、全国首批来华留学质量认证院校、首批全国深化创新创业教育改革特色典型经验高校，是教育部“援疆学科建设计划”40所重点高校之一，是中国首批具有博士、硕士、学士学位授予权的大学之一；设有“研究生院”和“国家大学科技园”。', '4', '1', '4');
